/** Regular expression to match URL patterns, developed using this handy tool https://regex101.com/r/lQ1nI3/1 */
const urlRegex = new RegExp([
    '^(https?:\\/\\/)',
    '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.?)+[a-z]{2,}',
    '|([a-z\\d]([a-z\\d-]*[a-z\\d])*)',
    '|(\\d{1,3}\\.){3}\\d{1,3})',
    '(\\:\\d+)?',
    '(\\/[-a-z\\d%_.~+]*)*',
    '(\\?(?:[;&a-z\\d%_.~+=\\-/]+[^#])*)?',
    '(\\#[-a-z\\d_]*)?$',
].join(''), 'i');
export default urlRegex;
